package javajungsuk_basic;

public class Ex2_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//컨트롤 + 알트+ 다운키 : 행단위복사
		//알트+시프트+다운 :컬럼 편집모드(토글)
		//print 출력후줄바꿈 안함
		//prinln 출력후 줄바꿈함
		System.out.println(5+3); //덧셈
		System.out.println(5-3);//뺄셈
		System.out.println(5*3);//곱셈
		System.out.println(5/3);//나눗셈 3으로 하면 1이나오고 3.0을 넣어야 1.66666 나옴
	}

}
